import phones from 'api/mockPhones'
import * as R from 'ramda'
export let FetchPhonesAPI = async () =>{
    return new Promise((resolve,reject )=>{
        resolve(phones)
    })
}

export let loadMorePhonesAPI = async (offset) =>{
    debugger
    return new Promise((resolve,reject )=>{
        resolve(phones)
    })
}

export let fetchPhoneByIDAPI = async(id)=> {
    return new Promise((resolve, reject) => {
        const phone = R.find(R.propEq('id', id), phones);
        resolve(phone)
    })
}

