import phones from 'api/mockPhones'
import categories from 'api/mockCategories'
import * as R from 'ramda'
export let FetchPhonesAPI = async () =>{
    return new Promise((resolve,reject )=>{
        resolve(phones)
    })
}

export let loadMorePhonesAPI = async () =>{
    return new Promise((resolve)=>{
        resolve(phones)
    })
}

export let fetchPhoneByIDAPI = async(id)=> {
    return new Promise((resolve, reject) => {
        const phone = R.find(R.propEq('id', id), phones);
        resolve(phone)
    })
}

export let fetchCategoriesAPI = async() =>{
    return new Promise((resolve)=>{
        resolve(categories)
    })
}

