import React from 'react'
import {Link} from "react-router-dom";
import {connect} from "react-redux";
import {getTotalBasketCount,getTotalBasketPrice} from 'selectors'
let BasketCart = ({totalBasketCount,totalPrice}) => {
    return (<div className='cart'>
        <div className='dropdown'>

            <Link to='/basket' type="button" className="btn btn-primary">
                Cart <span className="badge badge-light">{totalBasketCount} item(s) - ${totalPrice}</span>
            </Link>

        </div>
    </div>)
}

let mapStateToProps = state =>{
    return {
        totalBasketCount: getTotalBasketCount(state) ,
        totalPrice: getTotalBasketPrice(state)
    }
}
export default connect(mapStateToProps,null)(BasketCart)