import React from 'react'
import {connect} from "react-redux";
import * as R from 'ramda'
import {getCategories,getActiveCategoryId} from 'selectors'
import {Link, withRouter} from "react-router-dom";
import {compose} from "redux";

import classNames from 'classnames'
const Categories = ({categories,activeCategoryId}) =>{
    const getActiveState = R.propEq('id',activeCategoryId)
    const linkClass = classNames({
        'list-group-item' : true ,
        'active':getActiveState(categories)
    })
    debugger
    let renderCategory = (category,index) =>{
        return(
            <Link to={`/categories/${category.id}`}
            classname={linkClass}
            key={index}>
                {category.name}
            </Link>
        )
    }
    let renderAllCategory = ()=>{
        const linkClass = classNames({
            'list-group-item' : true ,
            'active':R.isNil(activeCategoryId)
        })
        return(
            <Link to={'/'} classname={linkClass}>All</Link>
        )
    }
    return (
        <div className='well'>
            <h4>Brand</h4>
            <div className='list-group'>
                {renderAllCategory()}
                {categories.map((category,index)=>renderCategory(category,index))}
            </div>
        </div>
    )
}
let mapStateToProps = (state,ownProps) =>{
    return {
        categories:getCategories(state),
        activeCategoryId: getActiveCategoryId(ownProps)
    }
}
export default compose(withRouter,connect(mapStateToProps,null))(Categories)