import React,{useState} from 'react'
import {connect} from "react-redux";

import {searchPhone} from 'actions'

let Search = (props) =>{
let [value,setValue] = useState(null)
let handleChange = (event) =>{
    setValue(event.target.value)
}
let handleSubmit =(event)=>{
    event.preventDefault()
        props.searchPhone(value)
    }
    return(
        <div className="input-group" >
                <input type="text" onChange={handleChange} className="form-control" placeholder="Type name of phone"
                       aria-label="Recipient's username" aria-describedby="button-addon2"/>
                    <div className="input-group-append">
                        <button className="btn btn-outline-secondary" onClick={handleSubmit} type="button" id="button-addon2">Search</button>
                    </div>
        </div>
    )
}
let mapStateToProps = (state)=>{
    return null
}
let mapDispatchToProps = {
    searchPhone
}
export default connect(mapStateToProps,mapDispatchToProps)(Search)


