import * as R from 'ramda'
import {FETCH_PHONE_SUCCESS} from 'actions'
let initialState = {
    ids:[]
}

let phonePage = (state = initialState,action) => {
    switch (action.type) {
        case FETCH_PHONE_SUCCESS : {
            return (
                R.merge(state,{ids:R.pluck('id',action.payload)})
            )
        }
        default :
            return state
    }
}
export default phonePage