import {
    FETCH_PHONE_SUCCESS,
    LOAD_MORE_PHONE_SUCCESS,
    FETCH_PHONE_BY_ID_SUCCESS
} from 'actions/actionsTypes'
import * as R from "ramda"
const initialState = []
let phones = (state = initialState , action)=> {

    switch (action.type) {
        case FETCH_PHONE_SUCCESS : {
            const newValues = R.indexBy(R.prop('id'), action.payload)
            return R.merge(state, newValues)
        }
        case LOAD_MORE_PHONE_SUCCESS : {
            const moreValues = R.indexBy(R.prop('id'), action.payload)
            return R.merge(state, moreValues)
            //return [...state,...action.payload]
        }
        case FETCH_PHONE_BY_ID_SUCCESS :
        return  R.assoc(action.payload.id,action.payload,state)

        default :
            return state
    }
}

export default phones