import * as R from 'ramda'
import {FETCH_PHONE_SUCCESS,SEARCH_PHONE} from 'actions/actionsTypes'
let initialState = {
    ids:[],
    search:''
}

let phonesPage = (state = initialState, action) => {
    switch (action.type) {
        case FETCH_PHONE_SUCCESS : {
            return(
                R.merge(state,{ids:R.pluck('id',action.payload)})
        )
        }
        case SEARCH_PHONE:{
          return  R.merge(state,{search:action.payload})
        }
        default :
            return state
    }
}
export default phonesPage