import * as R from 'ramda'
import {FETCH_PHONE_SUCCESS} from 'actions'
let initialState = {
    ids:[]
}

let phonesPage = (state = initialState, action) => {
    switch (action.type) {
        case FETCH_PHONE_SUCCESS : {
            return(
            //    ...state ,
            //    ids : action.payload.map(p=>p.id)
                R.merge(state,{ids:R.pluck('id',action.payload)})
        )
        }
        default :
            return state
    }
}
export default phonesPage