import * as R from 'ramda'
export const getPhoneByID = (state , id) =>{
    debugger
   return R.prop(id,state.phones)
}

export const getPhones = state => {
    let phones = state.phones //R.map(id=>getPhoneByID(state,id),state.phonesPage.ids)
    return phones
}

export const getRenderedPhonesLength = state => state.phonesPage.ids.length

export const getTotalBasketCount = state => R.length(state.basket)

export const getTotalBasketPrice = state => {
    const totalPrice = R.compose(
        R.sum,
        R.pluck('price'),
        R.map(id =>getPhoneByID(state,id))
    )(state.basket)
    return totalPrice
}